import Header from '@/components/common/Header.vue';
export default {
  name: 'Staff',
  components: {
    Header: Header
  },
  data: function data() {
    return {
      gridData: [],
      dialogFormVisible: false,
      name: '',
      dialogFormVisible1: false,
      dialogFormVisible2: false,
      name1: '',
      pageIndex: 1,
      groupId: -1,
      id: '',
      total: 1,
      tableData: [],
      yg: [],
      zu: '',
      zuId: 0,
      emNum: 0,
      reviewNum: 0,
      outerVisible4: false,
      status: true,
      isFinish: 1,
      yuangong: [],
      incomeMonth: null,
      entryNumMonth: null,
      orderNumMonth: null,
      list: [],
      data: ''
    };
  },
  created: function created() {
    this.getEmGroup(); // this.getinfo()
  },
  methods: {
    one: function one() {
      this.status = true;
      this.isFinish = 1;
      this.handleClickk(this.yuangong);
    },
    one1: function one1() {
      this.status = false;
      this.isFinish = 0;
      this.handleClickk(this.yuangong);
    },
    handleOpen: function handleOpen(key, keyPath) {
      console.log(key, keyPath);
    },
    handleClose: function handleClose(key, keyPath) {
      console.log(key, keyPath);
    },
    getEmGroup: function getEmGroup() {
      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token')
      };

      var _this = this;

      this.$http.getEmGroup(data).then(function (res) {
        _this.$_loading.close();

        if (res.errorCode == 0) {
          _this.gridData = res.data.list;
          _this.emNum = res.data.emNum;
          _this.reviewNum = res.data.reviewNum;
        }

        _this.getinfo();
      }).catch(function (err) {
        console.log(err);
      });
    },
    go: function go(e) {
      this.groupId = e;
      this.getinfo();
    },
    handleCurrentChange: function handleCurrentChange(e) {
      console.log(e);
      this.pageIndex = e;
      this.getinfo();
    },
    getinfo: function getinfo() {
      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        groupId: parseInt(this.groupId),
        pageIndex: parseInt(this.pageIndex),
        pageSize: 10
      };

      var _this = this;

      this.$http.review(data).then(function (res) {
        _this.$_loading.close();

        console.log('列表');
        console.log(res);

        if (res.errorCode == 0) {
          _this.tableData = res.data.data;
          _this.total = res.data.total;
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    // 新增分组接口
    addZ: function addZ() {
      this.dialogFormVisible = false;

      if (this.name < 1) {
        this.$message({
          message: '组名不能为空',
          type: 'warning',
          offset: '100'
        });
        return;
      }

      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        groupName: this.name
      };

      var _this = this;

      this.$http.addEmGroup(data).then(function (res) {
        _this.$_loading.close();

        _this.getEmGroup();

        if (res.errorCode == 0) {
          setTimeout(function () {
            _this.$message({
              message: '新增成功',
              type: 'success',
              offset: '100',
              duration: 3000
            });
          }, 1000);
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    handleCommand: function handleCommand(command) {
      this.zu = command;

      for (var i = 0; i < this.gridData.length; i++) {
        if (this.gridData[i].name == command) {
          this.zuId = this.gridData[i].groupId;
          break;
        }
      }
    },
    // 编辑用户组
    handleClick: function handleClick(e) {
      console.log(e);
      this.dialogFormVisible1 = true;
      this.name1 = e.name;
      this.id = e.groupId;
    },
    //员工详情
    handleClickk: function handleClickk(e) {
      console.log(e);
      this.yuangong = e;
      this.outerVisible4 = true; //请求接口

      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        orderDate: '2019-08',
        isFinish: this.isFinish,
        userId: parseInt(e.userId)
      };

      var _this = this;

      this.$http.getEmUserDetail(data).then(function (res) {
        console.log(12344);
        console.log(res);

        _this.$_loading.close();

        if (res.errorCode == 0) {
          _this.orderNumMonth = res.data.orderNumMonth;
          _this.incomeMonth = res.data.incomeMonth;
          _this.entryNumMonth = res.data.entryNumMonth;
          _this.list = res.data.list;
          _this.data = res.data.recOrderYear + '-' + res.data.recOrderMonth;
          console.log(33333);
          console.log(_this.list);
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    //编辑员工信息
    handleClickk1: function handleClickk1(e) {
      console.log(e);
      this.dialogFormVisible2 = true;
      this.yg = e;
      this.zu = e.groupName;
    },
    //修改用户所在组
    xiu: function xiu(e) {
      this.dialogFormVisible2 = false;
      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        certId: parseInt(e),
        groupId: parseInt(this.zuId)
      };

      var _this = this;

      this.$http.changeEmGroupByEpUser(data).then(function (res) {
        _this.$_loading.close();

        _this.getEmGroup();

        if (res.errorCode == 0) {
          _this.$message({
            message: '修改成功',
            type: 'success',
            offset: '100',
            duration: 3000
          });
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    // 修改分组接口
    addZ1: function addZ1() {
      this.dialogFormVisible1 = false;

      if (this.name1 < 1) {
        this.$message({
          message: '组名不能为空',
          type: 'warning',
          offset: '100'
        });
        return;
      }

      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        groupName: this.name1,
        groupId: parseInt(this.id)
      };

      var _this = this;

      this.$http.editEmGroup(data).then(function (res) {
        _this.$_loading.close();

        _this.getEmGroup();

        if (res.errorCode == 0) {
          _this.$message({
            message: '修改成功',
            type: 'success',
            offset: '100',
            duration: 3000
          });
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    // 删除用户组
    handleClick1: function handleClick1(e) {
      var _this2 = this;

      this.$confirm('此操作将删除该用户分组, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this2.delEmGroup(e);
      }).catch(function () {});
    },
    //删除员工
    delex: function delex(e) {
      var _this3 = this;

      this.dialogFormVisible2 = false;
      this.$confirm('此操作将删除该员工信息, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this3.delEmUser(e);
      }).catch(function () {});
    },
    delEmUser: function delEmUser(e) {
      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        emUserId: parseInt(e)
      };

      var _this = this;

      this.$http.delEmUser(data).then(function (res) {
        console.log(654321);
        console.log(res);

        _this.$_loading.close();

        _this.getEmGroup();

        if (res.errorCode == 0) {
          _this.$message({
            message: '删除成功',
            type: 'success',
            offset: '100',
            duration: 3000
          });
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    //通过用户申请
    handleClickkk: function handleClickkk(e) {
      var _this4 = this;

      this.$confirm('此操作将通过用户审核, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this4.review(e, 1);
      }).catch(function () {});
    },
    //拒绝用户申请
    handleClickkk1: function handleClickkk1(e) {
      var _this5 = this;

      this.$confirm('此操作将拒绝用户审核, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this5.review(e, -1);
      }).catch(function () {});
    },
    //审核员工
    review: function review(e, i) {
      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        certId: parseInt(e.id),
        pass: parseInt(i)
      };

      var _this = this;

      this.$http.review1(data).then(function (res) {
        _this.$_loading.close();

        _this.getEmGroup();

        if (res.errorCode == 0) {
          if (i == 1) {
            _this.$message({
              message: '审核成功',
              type: 'success',
              offset: '100',
              duration: 3000
            });
          } else {
            _this.$message({
              message: '拒绝成功',
              type: 'success',
              offset: '100',
              duration: 3000
            });
          }
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    // 删除用户分组
    delEmGroup: function delEmGroup(e) {
      this.$_loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      var data = {
        accessToken: '1565742674|145B1691263AEC04CC1722BA2EF68A86',
        id_token: this.$cookies.get('access_token'),
        groupId: parseInt(e.groupId)
      };

      var _this = this;

      this.$http.delEmGroup(data).then(function (res) {
        _this.$_loading.close();

        _this.getEmGroup();

        if (res.errorCode == 0) {
          setTimeout(function () {
            _this.$message({
              message: '删除成功',
              type: 'success',
              offset: '100',
              duration: 3000
            });
          }, 1000);
        }
      }).catch(function (err) {
        console.log(err);
      });
    }
  }
};
